function updateHeaderDisplay(){const header2Tags=document.querySelectorAll("header header-2");const header3Tags=document.querySelectorAll("header header-3 > .container");const headerLogo=document.querySelectorAll("header-logo");const prayerWeatherInfo=document.querySelectorAll("header header-1 .prayer-weather-info");const dateTimeInfo=document.querySelectorAll("header header-1 .date-time-info");const headerMobile=document.querySelector("header-mobile");const screenWidth=window.innerWidth;const footerInfo=document.querySelectorAll("flex-row.top");if(screenWidth<991){header2Tags.forEach(header2=>header2.classList.add("hidden"));header3Tags.forEach(header3=>header3.classList.add("hidden"));prayerWeatherInfo.forEach(info=>info.classList.add("hidden"));dateTimeInfo.forEach(info=>info.classList.add("hidden"));headerLogo.forEach(headerLogo=>headerLogo.classList.remove("hidden"));headerMobile.classList.remove("hidden")}else{header2Tags.forEach(header2=>header2.classList.remove("hidden"));header3Tags.forEach(header3=>header3.classList.remove("hidden"));prayerWeatherInfo.forEach(info=>info.classList.remove("hidden"));dateTimeInfo.forEach(info=>info.classList.remove("hidden"));headerLogo.forEach(headerLogo=>headerLogo.classList.add("hidden"));headerMobile.classList.add("hidden")}}updateHeaderDisplay();window.addEventListener("resize",updateHeaderDisplay);